mapper.insert:
INSERT INTO moka.comment(
branchId, lineId, userId, context, praised, forward, createDate, review, lineSendId
) VALUES (#{branchId}, #{lineId}, #{userId}, #{context}, #{praised}, #{forward}, #{createDate}, #{review}, #{lineSendId});

mapper.select:
SELECT
c.* ,
( SELECT COUNT(cr.id) FROM moka.commentrelation cr WHERE 1 = 1 AND cr.commentId = c.id AND cr.userId = #{userId} AND cr.comType = 1 AND cr.type = 3) AS isPraised,
( SELECT COUNT(cr.id) FROM moka.commentrelation cr WHERE 1 = 1 AND cr.commentId = c.id AND cr.userId = #{userId} AND cr.comType = 3 AND cr.type = 3) AS isForward
FROM moka.comment c
WHERE 1 = 1

    AND c.branchId = #{branchId}


    AND c.lineId = #{lineId}


    AND c.lineSendId = #{lineSendId}

LIMIT #{pageStart} , #{pageSize};

mapper.insert:
INSERT INTO moka.commentrelation(
userId, lineId, commentId, comType, lineSendId, type, replyId
) VALUES (#{userId}, #{lineId}, #{commentId}, #{comType}, #{lineSendId}, #{type}, #{replyId});

mapper.delete:
DELETE FROM moka.commentrelation
WHERE 1 = 1
AND userId = #{userId}
AND comType = #{comType}

    AND lineId = #{lineId}
    AND type = 1


    AND lineSendId = #{lineSendId}
    AND type = 2


    AND commentId = #{commentId}
    AND type = 3


    AND replyId = #{replyId}
    AND type = 4


    AND 1 = 2

mapper.select:
SELECT COUNT(cr.id) FROM moka.commentrelation cr
WHERE 1 = 1
AND cr.comType = #{comType}
AND cr.userId = #{userId}

    AND cr.lineId = #{lineId}
    AND type = 1


    AND cr.lineSendId = #{lineSendId}
    AND type = 2


    AND cr.commentId = #{commentId}
    AND type = 3


    AND cr.replyId = #{replyId}
    AND type = 4


    AND 1 = 2
