mapper.select:
SELECT
c.* ,
( SELECT COUNT(cr.id) FROM moka.commentrelation cr WHERE 1 = 1 AND cr.commentId = c.id AND cr.userId = #{userId} AND cr.comType = 1) AS isPraised,
( SELECT COUNT(cr.id) FROM moka.commentrelation cr WHERE 1 = 1 AND cr.commentId = c.id AND cr.userId = #{userId} AND cr.comType = 3) AS isForward
FROM moka.comment c
WHERE 1 = 1

    AND c.branchId = #{branchId}


    AND c.lineId = #{lineId}


    AND c.lineSendId = #{lineSendId}

LIMIT #{pageStart} , #{pageSize};

mapper.insert:
INSERT INTO moka.commentrelation(
userId, lineId, commentId, comType, lineSendId, type
) VALUES (#{userId}, #{lineId}, #{commentId}, #{comType}, #{lineSendId}, #{type});

mapper.delete:
DELETE FROM moka.commentrelation
WHERE 1 = 1
AND userId = #{userId}
AND comType = #{comType}

    AND lineId = #{lineId}


    AND lineSendId = #{lineSendId}


    AND commentId = #{commentId}


    AND 1 = 2

mapper.select:
SELECT COUNT(cr.id) FROM moka.commentrelation cr
WHERE 1 = 1
AND cr.comType = #{comType}
AND cr.userId = #{userId}

    AND cr.lineId = #{lineId}


    AND cr.lineSendId = #{lineSendId}


    AND cr.commentId = #{commentId}


    AND 1 = 2
