mapper.insert:
INSERT INTO moka.reply(
branchId, lineId, lineSendId, userId, userToId, context, praised, createDate, updateDate, state
) VALUES (#{branchId}, #{lineId}, #{lineSendId}, #{userId}, #{userToId}, #{context}, #{praised}, #{createDate}, #{updateDate}, #{state});

mapper.select:
SELECT r.*
(SELECT COUNT(cr.id) FROM moka.commentrelation cr WHERE 1 = 1 AND cr.replyId = r.id AND cr.userId = #{userId} AND cr.comType = 1 ) AS isPraised
FROM moka.reply r WHERE 1 = 1

    AND commentId = #{commentId}

LIMIT #{pageStart} , #{pageSize};
